INSERT INTO `courses` VALUES ('20', '6', '0', '0', '易中天品三国', '8.8', '《易中天品三国》，央视CCTV-10历史演播类节目，《百家讲坛》系列之一。主讲人易中天，还原真实曹操，替周瑜辩诬，正说诸葛亮，重评司马懿。纵论天下，闲话三分。细品是非功过，总结成败得失。厦门大学易中天教授将以故事说人物，以人物说历史，以历史说文化，以文化说人性。纵论三国天下大事，细品英雄成败得失。易中天教授将从平民角度为大家解读。《易中天品三国》，央视CCTV-10历史演播类节目，《百家讲坛》系列之一。主讲人易中天，还原真实曹操，替周瑜辩诬，正说诸葛亮，重评司马懿。纵论天下，闲话三分。细品是非功过，总结成败得失。厦门大学易中天教授将以故事说人物，以人物说历史，以历史说文化，以文化说人性。纵论三国天下大事，细品英雄成败得失。易中天教授将从平民角度为大家解读。', null, null, null, null, null, '/Uploads/images/4/52ce9d2487b94.jpg', null, '0', '5', '0', '0', null, '1', '90', '0', '0', '1942', '2014-01-28 07:27:18', '0');
